		printf(
			'<input class="regular-text" type="text" name="staging_site_redirects_option_name[main_site_url_will_redirect_all_non_logged_in_users_to_this_site_0]" id="main_site_url_will_redirect_all_non_logged_in_users_to_this_site_0" value="%s">',
			isset( $this->staging_site_redirects_options['main_site_url_will_redirect_all_non_logged_in_users_to_this_site_0'] ) ? esc_attr( $this->staging_site_redirects_options['main_site_url_will_redirect_all_non_logged_in_users_to_this_site_0']) : ''
		);
	}

}
